mapper.update:
update kg_user
set user_name = #{userName,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR}
where user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select user_id, user_name, password, phone
from kg_user
where user_id = #{userId,jdbcType=INTEGER}

